import React, { useState } from "react";
import { Input } from "antd";
import { use } from "react-router-dom"; // Sử dụng để chuyển hướng
import "./Search.css";
import bell from "../assets/images/bell.png";
import mail from "../assets/images/mail.png";

const { Search } = Input;

function SearchComponent() {
  const [searchResults, setSearchResults] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const history = useHistory();

  const handleSearch = async (value) => {
    try {
      const response = await fetch(`http://localhost:6677/products/search?key=${value}`);
      if (response.ok) {
        const data = await response.json();
        setSearchResults(data); // Lưu kết quả tìm kiếm vào state
        setShowSuggestions(true); // Hiển thị gợi ý
      } else {
        console.error("Lỗi khi gọi API:", response.status);
      }
    } catch (error) {
      console.error("Lỗi:", error);
    }
  };

  const handleSelectSuggestion = (productId) => {
    setShowSuggestions(false); // Đóng gợi ý khi đã chọn
    history.push(`/products?product=${productId}`); // Chuyển hướng sang trang /products
  };

  return (
    <div className="search-component">
      <Search
        className="search-inside"
        placeholder="Nhập để tìm kiếm..."
        onSearch={handleSearch}
        style={{ border: "none", boxShadow: "none" }}
        onChange={(e) => handleSearch(e.target.value)} // Gọi tìm kiếm khi người dùng nhập
      />
      <div className="icon-component">
        <img className="bell-icon" src={bell} alt="bell" />
        <img className="mail-icon" src={mail} alt="mail" />
      </div>
      
      {showSuggestions && searchResults.length > 0 && (
        <div className="search-suggestions">
          {searchResults.map((product) => (
            <div
              key={product.id}
              className="suggestion-item"
              onClick={() => handleSelectSuggestion(product.id)}
            >
              {product.name}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchComponent;
